package com.assignment;

public class Assignment5 {
	int a=10;
	int b=20;
	void addition() {
		System.out.println(a+b);
	}
	void subtraction() {
		System.out.println(a-b);
	}
	void multiplication() {
		System.out.println(a*b);
	}
	void division() {
		System.out.println(a/b);
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Assignment5 ob = new Assignment5();
		ob.addition();
		ob.subtraction();
		ob.multiplication();
		ob.division();
	}

}
